import { Board } from './Board.js';
import { useState, useEffect } from 'react';

const BOARD = null

export const HandleBoard = ({ activeGame, currentLevel, clickedX, clickedY, BOARD }) => {
	useEffect(() => {
		if (!BOARD)
			BOARD = new Board()
	}, [clickedX, clickedY])

	useEffect(() => {
		if (!BOARD)
			BOARD = new Board()
		if (!activeGame) {
			console.log("drawing static puzzle")
			BOARD.drawStaticPuzzle()
		}
		else {
			console.log("drawing dynamic puzzle level: ", currentLevel)
			BOARD.drawDynamicPuzzle(currentLevel)
		}
	}, [activeGame])
	return (
		<></>
	)
}
