import { useEffect } from "react";

export const RightSideButtons = ({ activeGame, selectedCell }) => {
	const buttonNumberClick = (number) => {
		console.log("clicked: ", number)
		if (!activeGame || !selectedCell) {
			console.log("game not active or no selected cell so button click ignored")
			return
		}
		console.log("")
	}

	return (
		<div id="right-side-elements">
			<div id="button-numbers-container">
				<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>1</button>
				<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>2</button>
				<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>3</button>
				<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>4</button>
				<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>5</button>
				<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>6</button>
				<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>7</button>
				<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>8</button>
				<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>9</button>
			</div>
			{/* <div id="timer"></div> */}
		</div>
	)
}

export const LeftSideButtons = ({ activeGame, setActiveGame, currentLevel, setCurrentLevel }) => {
	useEffect(() => {
		const bodyContent = document.getElementById("body-content")
		const gameButtons = document.getElementById('game-buttons');

		if (window.innerHeight > window.innerWidth) {
			bodyContent.style.flexDirection = 'column'
			gameButtons.style.flexDirection = 'row'
		} else {
			bodyContent.style.flexDirection = 'row'
			gameButtons.style.flexDirection = 'column'
		}
	}, [activeGame])

	function updateLevel(e) {
		console.log("updating level: ", e.target.value)
		switch (e.target.value) {
			case "Easy":
				setCurrentLevel(0)
				break
			case "Medium":
				setCurrentLevel(1)
				break
			case "Hard":
				setCurrentLevel(2)
				break
			case "Extreme":
				setCurrentLevel(3)
				break
			default:
				break
		}
	}

	return (
		<div id="game-buttons">
			{
				!activeGame && (
					<button id="start" className="playButtons" onClick={() => setActiveGame(true)} > Start </button>
				)
			}
			{
				activeGame && (
					<button className="playButtons" onClick={() => setActiveGame(false)} > Give Up </button>
				)
			}
			<select id="select-difficulty" className="playButtons" onChange={(e) => updateLevel(e)}>
				<option value="Easy">Easy</option>
				<option value="Medium">Medium</option>
				<option value="Hard">Hard</option>
				<option value="Extreme">Extreme</option>
			</select>
		</div >
	)
}
