import { useState, useEffect } from 'react';
import './App.css';

const Person = (props) => {
	return (
		<>
			<h1>name: {props.name}</h1>
			<h2>last name: {props.lastName}</h2>
			<h2>age: {props.age}</h2>
		</>
	)
}

const Counter = () => {
	const [counter, setCounter] = useState(0)

	useEffect(() => {
		setCounter(() => 100)
	}, [])

	return (
		<>
			<button onClick={() => setCounter((prev) => prev - 1)}>-</button>
			<p>{counter}</p>
			<button onClick={() => setCounter((prev) => prev + 1)}>+</button>
		</>
	)
}

const App = () => {
	return (
		<div className='App'>
			<Counter />
			<Person
				name={'John'}
				lastName={'Doe'}
				age={'30'}
			/>
			<Person name={'Bob'} lastName={'Someone'} age={'35'} />
		</div>
	)
}

export default App;
