import { PAGE_GAME, PAGE_ACCOUNT, PAGE_HOME, TITLES, PORT_SERVER } from './Const';
import { useState, useEffect } from 'react';
import { Sections } from './Const.js'
import './Account.css'

const SECTIONS = new Sections()

const SignupSection = (({ currentSection, setCurrentSection }) => {
	const [username, setUsername] = useState("")
	const [password, setPassword] = useState("")
	const [email, setEmail] = useState("")
	const [statusMessage, setStatusMessage] = useState("")
	const updateValue = (type, value) => {
		switch (type) {
			case "username":
				setUsername(value)
				break
			case "password":
				setPassword(value)
				break
			case "email":
				setEmail(value)
				break
		}
	}
	const submitSignup = (async (e) => {
		e.preventDefault()
		const body = JSON.stringify({ username: username, password: password, email: email })
		console.log("submit signup: ", body)
		const data = await fetch(`http://127.0.0.1:${PORT_SERVER}/signup`, {
			method: "POST",
			headers: { "Content-Type": "application/json" },
			body: body
		}).then((response) => response.json())
		console.log(data)
		if (data["status"] == "success") {
			console.log("signup successful")
			setStatusMessage(data["message"])
		}
		else {
			console.log("signup error: ", data["message"])
			console.log(data)
			console.log(data["message"])
		}
		// setUsername("")
		// setPassword("")
		// setEmail("")
	})
	return (
		<form className="account-form" onSubmit={(e) => submitSignup(e)} >
			<div className="account-form-section">
				<h2 className="account-input-header">Username</h2>
				<input className="account-input" type="text" onChange={(e) => updateValue("username", e.target.value)} />
			</div>
			<div className="account-form-section">
				<h2 className="account-input-header">Password</h2>
				<input className="account-input" type="text" onChange={(e) => updateValue("password", e.target.value)} />
			</div>
			<div className="account-form-section">
				<h2 className="account-input-header">Email</h2>
				<input className="account-input" type="text" onChange={(e) => updateValue("email", e.target.value)} />
			</div>
			<button className="submit-button" >Submit</button>
			<p className="switch-login-signup">click here to login</p>
		</form >
	)
})

const LoginSection = (({ currentSection, setCurrentSection, setLoggedIn }) => {
	const [username, setUsername] = useState("")
	const [password, setPassword] = useState("")
	const handleInputChange = ((e, type) => {
		if (type == "username")
			setUsername(e.target.value)
		else if (type == "password")
			setPassword(e.target.value)
	})
	const submitLogin = async (e) => {
		console.log("submitting login");
		e.preventDefault()
		await fetch("http://127.0.0.1:5463/login", {
			method: "POST",
			headers: { "Content-Type": "application/json" },
			body: JSON.stringify({ username, password }),
		})
			.then((response) => {
				if (!response.ok) {
					throw new Error("Login failed");
				}
				return response.json();
			})
			.then((data) => {
				console.log("Login successful:", data);
			})
			.catch((error) => {
				console.error("Error during login:", error);
			})
	}
	return (
		<form className="account-form" onSubmit={(e) => { submitLogin(e) }}>
			<div className="account-form-section">
				<h3 className="account-input-header">Username</h3>
				<input className="account-input" required type="text" onChange={(e) => handleInputChange(e, "username")} />
			</div>
			<div className="account-form-section">
				<h3 className="account-input-header">Password</h3>
				<input className="account-input" required type="text" onChange={(e) => handleInputChange(e, "password")} />
			</div>
			<button className="submit-button" >Submit</button>
			<p className="switch-login-signup" onClick={() => setCurrentSection(SECTIONS.signup)}>click here to signup</p>
		</form>
	)
})

const GetSection = (({ currentSection, setCurrentSection }) => {
	console.log("get section: ", currentSection)
	console.log(SECTIONS.login)
	if (currentSection == SECTIONS.login) {
		return (
			<div id="account-body">
				<LoginSection currentSection={currentSection} setCurrentSection={setCurrentSection} />
			</div>
		)
	}
	if (currentSection == SECTIONS.signup) {
		return (
			<div id="account-body">
				<SignupSection currentSection={currentSection} setCurrentSection={setCurrentSection} />
			</div>
		)
	}
})

export const Account = (loggedIn, setLoggedIn) => {
	const [currentSection, setCurrentSection] = useState(loggedIn == true ? SECTIONS.profile : SECTIONS.login)

	return (
		<div id="parent">
			<div id="account">
				<h1 id="account-head">{TITLES[currentSection]}</h1>
				<GetSection currentSection={currentSection} setCurrentSection={setCurrentSection} setLoggedIn={setLoggedIn} />
			</div>
		</div>
	)
}
