import { useState, useEffect } from 'react';
import { Navbar, setUserSectionWidth } from './Navbar';
import { Board } from './Board.js';
import { HandleBoard, setClickPos } from './HandleBoard';
import { leftSideButtons } from './GameButtons.js'
import './App.css'

const App = () => {
	const [activeGame, setActiveGame] = useState(false)
	const [currentLevel, setCurrentLevel] = useState(0)
	const [canvasClickedX, setCanvasClickedX] = useState(0)
	const [canvasClickedY, setCanvasClickedY] = useState(0)
	const [triggerClick, setTriggerClick] = useState(0)
	const [selectedCell, setSelectedCell] = useState(false)

	const buttonNumberClick = (number) => {
		console.log("clicked: ", number)
		if (!activeGame || !selectedCell) {
			console.log("game not active or no selected cell so button click ignored")
			return
		}
		console.log("")
	}

	return (
		<>
			<Navbar />
			<div id="body-content">
				<leftSideButtons activeGame={activeGame} setActiveGame={setActiveGame} currentLevel={currentLevel} setCurrentLevel={setCurrentLevel} />
				<canvas id="my-canvas" onClick={(e) => { setClickPos(e.clientX, e.clientY, activeGame, triggerClick, setCanvasClickedX, setCanvasClickedY, setTriggerClick) }} ></canvas>
				<HandleBoard activeGame={activeGame} currentLevel={currentLevel} setCurrentLevel={setCurrentLevel} canvasClickedX={canvasClickedX} canvasClickedY={canvasClickedY} triggerClick={triggerClick} setCanvasClickedX={setCanvasClickedX} setCanvasClickedY={setCanvasClickedY} setSelectedCell={setSelectedCell} />
				<div id="right-side-elements">
					<div id="button-numbers-container">
						<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>1</button>
						<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>2</button>
						<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>3</button>
						<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>4</button>
						<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>5</button>
						<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>6</button>
						<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>7</button>
						<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>8</button>
						<button className="button-numbers" onClick={(e) => buttonNumberClick(e.innerText)}>9</button>
					</div>
					{/* <div id="timer"></div> */}
				</div>
			</div>
		</>
	)
}

export default App;
