import { useState, useEffect } from 'react';
import { Navbar, setUserSectionWidth } from './Navbar';
import './App.css'

// puzzle -> blocks -> cells

class Cell {
	constructor() {
		this.value = null
		this.notes = []
		this.mod; // false or true
	}
	fill(_value) {
		this.value = _value
	}
	addNotes(number) {
		this.notes.push(number)
	}
	rmNotes() {
		this.notes = []
	}
}

class Game {
	constructor(difficulty) {
		this.difficulty = difficulty;
	}
	updateArrayPuzzle() {

	}
}

class AbstractBoard {
	constructor() {
		const [startX, startY, length] = this.getDimensions()
		this.startX;
		this.startY;
		this.length;
		this.staticPuzzle = getStaticPuzzle();
	}
	getDimensions() {
		let startX;
		let startY;
		let length;

		let windowHeight = window.innerHeight
		let windowWidth = window.innerWidth
		let nav_menu = document.getElementById('nav-menu')
		let nav_rects = nav_menu.getBoundingClientRect()

		if (windowHeight >= windowWidth)
			length = nav_rects.width * 0.8
		else
			length = (windowHeight - nav_rects.height) * 0.8

		startX = (nav_rects.width - width) / 2
		startY = nav_rects.height + (windowHeight - (+ length)) / 2
		return [startX, startY, length]
	}
	makePuzzleStructure() {
		let ret = []
		for (let i = 1; i <= 9; i++) {
			let row = []
			for (let j = 1; j <= 9; j++) {
				row.push(new Cell())
			}
			ret.push(row)
		}
		return ret;
	}
	fillPuzzle(sudokuGrid, puzzle) {
		for (let y = 0; y < 9; y++) {
			let row = []
			for (let x = 0; x < 9; x++) {
				row.push(puzzle[y][x].fill(sudokuGrid[y][x]))
			}
			this.puzzle.push(row)
		}
	}
	getStaticPuzzle() {
		const sudokuGrid = [
			[5, 3, 4, 6, 7, 8, 9, 1, 2],
			[6, 7, 2, 1, 9, 5, 3, 4, 8],
			[1, 9, 8, 3, 4, 2, 5, 6, 7],
			[8, 5, 9, 7, 6, 1, 4, 2, 3],
			[4, 2, 6, 8, 5, 3, 7, 9, 1],
			[7, 1, 3, 9, 2, 4, 8, 5, 6],
			[9, 6, 1, 5, 3, 7, 2, 8, 4],
			[2, 8, 7, 4, 1, 9, 6, 3, 5],
			[3, 4, 5, 2, 8, 6, 1, 7, 9]
		];
		let puzzle = this.makePuzzleStructure()
		this.fillPuzzle(sudokuGrid, puzzle)
		return puzzle;
	}
}

class Board extends AbstractBoard {
	constructor() {
		super()
	}
}

const handleBoard = () => {
	const [active, setActive] = useState(false)
	let board = Board;

	useEffect(() => {
		if (!active)
			board.drawStaticBoard()
		else
			board.drawDynamicBoard()
	}, [])

	return (
		<div id="Board-container">
		</div>
	)
}

const App = () => {
	return (
		<>
			<Navbar />
			<Board />
		</>
	)
}

export default App;
