import { Board } from './Board.js';
import { useState, useEffect } from 'react';

export let BOARD = null

/**
 * @param {boolean} activeGame
 * @param {number} currentLevel
 * @param {number} clickedX
 * @param {number} clickedY
 * @param {Board|null} BOARD
*/
export const HandleBoard = ({ activeGame, currentLevel, canvasClickedX, canvasClickedY, triggerClick, setCanvasClickX, setCanvasClickY, setTriggerClick }) => {
	console.log("handleBoard called")


	const setClickPos = (clientX, clientY) => {
		if (!activeGame)
			return
		if (triggerClick == 0)
			setTriggerClick(1)
		else
			setTriggerClick(0)
		const canvas = document.getElementById("my-canvas");
		const rect = canvas.getBoundingClientRect();
		const x = clientX - rect.left;
		const y = clientY - rect.top;
		setCanvasClickedX(x);
		setCanvasClickedY(y);
	}

	useEffect(() => {
		if (!BOARD)
			BOARD = new Board()
		if (activeGame)
			BOARD.updateSelection(canvasClickedX, canvasClickedY)
	}, [canvasClickedX, canvasClickedY, triggerClick])
	useEffect(() => {
		if (!BOARD)
			BOARD = new Board()
		if (!activeGame)
			BOARD.giveUp();
	}, [activeGame])
	useEffect(() => {
		if (!BOARD)
			BOARD = new Board()
		BOARD.draw(activeGame, currentLevel)
	}, [activeGame, canvasClickedX, canvasClickedY, triggerClick])
	return (
		<></>
	)
}
