import { useEffect } from "react";
import { GameState } from "./Const";

export const GameButtons = ({ activeGame, setActiveGame }) => {
	useEffect(() => {
		const bodyContent = document.getElementById("body-content")
		const gameButtons = document.getElementById('game-buttons');
		const startRestart = document.getElementById('start-restart');
		const selectDifficulty = document.getElementById('select-difficulty');

		startRestart.classList.add("playButtons")
		selectDifficulty.classList.add("playButtons")

		if (window.innerHeight > window.innerWidth) {
			// Mobile app logic
			bodyContent.style.flexDirection = 'column'
			gameButtons.style.flexDirection = 'row'
		} else {
			bodyContent.style.flexDirection = 'row'
			gameButtons.style.flexDirection = 'column'
			startRestart.style.top = "50%"
		}
		startRestart.addEventListener("click", () => {
			console.log(activeGame)
			setActiveGame(true)
		})
		return () => {
			startRestart.removeEventListener('click', () => { });
		};
	}, [activeGame])

	return (
		<div id="game-buttons">
			{
				!activeGame && (
					<button id="start-restart" onClick={() => setActiveGame(true)} > {'Restart'} </button>
				)
			}
			{
				activeGame && (
					<button className="playButtons" onClick={() => setActiveGame(false)} > Give Up </button>
				)
			}
			<select id="select-difficulty">
				<option value="Easy">Easy</option>
				<option value="Intermediate">Medium</option>
				<option value="Advanced">Hard</option>
			</select>
		</div >
	)
}
