import { useState, useEffect } from 'react';
import { Navbar, setUserSectionWidth } from './Navbar';
import './App.css'

// puzzle -> blocks -> cells

class Cell {
	constructor() {
		this.value = null
		this.notes = []
		this.mod = null; // false or true
	}
	fill(_value) {
		this.value = _value
	}
	addNotes(number) {
		this.notes.push(number)
	}
	rmNotes() {
		this.notes = []
	}
}

class Game {
