			for (let x = 0; x < 9; x++) {

			}
		}
	}
}

class Board extends AbstractBoard {
	constructor() {
		super()
	}
	drawStaticPuzzle() {
		this.drawPuzzle(this.staticPuzzle)
	}
}

const HandleBoard = () => {
	const [active, setActive] = useState(false)

	useEffect(() => {
		let board = new Board();
		// console.log(board.staticPuzzle)
		if (!active)
			board.drawStaticPuzzle()
		// else
		// 	board.drawDynamicPuzzle()
	}, [])

	return (
		<div id="Board-container">
		</div>
	)
}

const App = () => {
	return (
		<>
			<Navbar />
			<HandleBoard />
		</>
	)
}

export default App;
