import { useState, useEffect } from 'react';
import { Navbar, setUserSectionWidth } from './Navbar';
import { HandleBoard } from './HandleBoard';
import './App.css'

const GameButtons = () => {
	const [active, setActive] = useState(false)

	useEffect(() => {
		let game_items = document.getElementById('game-items')
		let start_restart = document.getElementById('start-restart')
		const canvas = document.getElementById('my-canvas')
		if (window.innerHeight > window.innerWidth) {
			// mobile app
		} else {
			game_items.style.left = `{canvas.style.left + canvas.getBoundingClientRect().width * 2}px`
			start_restart.style.left = `{canvas.style.left + canvas.getBoundingClientRect().width * 2}px`
			start_restart.style.postion = relative
		}
	}, [])

	return (
		<div id="game-items">
			<button id="start-restart">`{active ? 'restart' : 'start'}`</button>
			{/* <select id="select-difficulty"> */}
			{/* 	<option value="Easy">Easy</option> */}
			{/* 	<option value="Intermediate">Intermediate</option> */}
			{/* 	<option value="Advanced">Advanced</option> */}
			{/* </select> */}
		</div>
	)
}

const App = () => {
	return (
		<>
			<Navbar />
			<div id="body-content">
				<HandleBoard />
				<GameButtons />
			</div>
		</>
	)
}

export default App;
